  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND HOSPITAL_NO like '%${hospitalNo}%'
  AND HOSPITAL_NO=#{hospitalNoEq,jdbcType=VARCHAR}
