SELECT ID,PATIENT_ID,FOLDER_ID,HOSPITAL_ID,DEPARTMENT_ID,DOCTOR_ID,TEMP_ID,VIEW_PASSWORD,CASE_DATE,CASE_TYPE,CASE_NAME,CATGORY,ITEM,TECHNICIAN,DESCRIPTION,HOSPITAL_NO,BED_NO,PATIENT_CATGORY,PIC_URL,APPLY_URL,REPORT_URL,CHECK_FORM_URL,OTHERS,SHARE_STATUS,SHARE_COUNT,FAVORITE_COUNT,FAVORITE_STATUS,RELATE_STATUS,VIEW_STATUS,DOWNLOAD_COUNT,DOWN_STATUS,CASE_DESC,CREATOR,CREATE_TIME,MODIFIER,MODIFY_TIME,IC_ID,NIL_URL,SOURCE,STUDY,PID,PN,GP FROM CPF_CASE_HISTORY

  AND ID=#{id,jdbcType=INTEGER}
  AND ID in (${id_instr})
  AND PATIENT_ID=#{patientId,jdbcType=INTEGER}
  AND PATIENT_ID in (${patientId_instr})
  AND FOLDER_ID=#{folderId,jdbcType=INTEGER}
  AND FOLDER_ID in (${folderId_instr})
  AND HOSPITAL_ID=#{hospitalId,jdbcType=INTEGER}
  AND HOSPITAL_ID in (${hospitalId_instr})
  AND DEPARTMENT_ID=#{departmentId,jdbcType=INTEGER}
  AND DOCTOR_ID=#{doctorId,jdbcType=INTEGER}
  AND DOCTOR_ID in (${doctorId_instr})
  AND TEMP_ID like '%${tempId}%'
  AND TEMP_ID=#{tempIdEq,jdbcType=VARCHAR}
  AND VIEW_PASSWORD like '%${viewPassword}%'
  AND VIEW_PASSWORD=#{viewPasswordEq,jdbcType=VARCHAR}
  AND CASE_DATE=#{caseDate,jdbcType=DATE}
  AND CASE_DATE>=#{caseDateFrom,jdbcType=DATE}
  AND CASE_DATE<=#{caseDateTo,jdbcType=DATE}
  AND CASE_TYPE like '%${caseType}%'
  AND CASE_TYPE=#{caseTypeEq,jdbcType=VARCHAR}
  AND CASE_NAME like '%${caseName}%'
  AND CASE_NAME=#{caseNameEq,jdbcType=VARCHAR}
  AND CATGORY like '%${catgory}%'
  AND CATGORY=#{catgoryEq,jdbcType=VARCHAR}
  AND ITEM like '%${item}%'
  AND ITEM=#{itemEq,jdbcType=VARCHAR}
  AND TECHNICIAN like '%${technician}%'
  AND TECHNICIAN=#{technicianEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND HOSPITAL_NO like '%${hospitalNo}%'
  AND HOSPITAL_NO=#{hospitalNoEq,jdbcType=VARCHAR}
  AND BED_NO like '%${bedNo}%'
  AND BED_NO=#{bedNoEq,jdbcType=VARCHAR}
  AND PATIENT_CATGORY like '%${patientCatgory}%'
  AND PATIENT_CATGORY=#{patientCatgoryEq,jdbcType=VARCHAR}
  AND PIC_URL like '%${picUrl}%'
  AND PIC_URL=#{picUrlEq,jdbcType=VARCHAR}
  AND APPLY_URL like '%${applyUrl}%'
  AND APPLY_URL=#{applyUrlEq,jdbcType=VARCHAR}
  AND REPORT_URL like '%${reportUrl}%'
  AND REPORT_URL=#{reportUrlEq,jdbcType=VARCHAR}
  AND CHECK_FORM_URL like '%${checkFormUrl}%'
  AND CHECK_FORM_URL=#{checkFormUrlEq,jdbcType=VARCHAR}
  AND OTHERS like '%${others}%'
  AND OTHERS=#{othersEq,jdbcType=VARCHAR}
  AND SHARE_STATUS like '%${shareStatus}%'
  AND SHARE_STATUS=#{shareStatusEq,jdbcType=VARCHAR}
  AND SHARE_COUNT=#{shareCount,jdbcType=INTEGER}
  AND FAVORITE_COUNT=#{favoriteCount,jdbcType=INTEGER}
  AND FAVORITE_STATUS like '%${favoriteStatus}%'
  AND FAVORITE_STATUS=#{favoriteStatusEq,jdbcType=VARCHAR}
  AND RELATE_STATUS like '%${relateStatus}%'
  AND RELATE_STATUS=#{relateStatusEq,jdbcType=VARCHAR}
  AND VIEW_STATUS like '%${viewStatus}%'
  AND VIEW_STATUS=#{viewStatusEq,jdbcType=VARCHAR}
  AND DOWNLOAD_COUNT=#{downloadCount,jdbcType=INTEGER}
  AND DOWN_STATUS like '%${downStatus}%'
  AND DOWN_STATUS=#{downStatusEq,jdbcType=VARCHAR}
  AND CASE_DESC like '%${caseDesc}%'
  AND CASE_DESC=#{caseDescEq,jdbcType=LONGVARCHAR}
  AND CREATOR like '%${creator}%'
  AND CREATOR=#{creatorEq,jdbcType=VARCHAR}
  AND CREATE_TIME=#{createTime,jdbcType=TIMESTAMP}
  AND CREATE_TIME>=#{createTimeFrom,jdbcType=TIMESTAMP}
  AND CREATE_TIME<=#{createTimeTo,jdbcType=TIMESTAMP}
  AND MODIFIER like '%${modifier}%'
  AND MODIFIER=#{modifierEq,jdbcType=VARCHAR}
  AND MODIFY_TIME=#{modifyTime,jdbcType=TIMESTAMP}
  AND MODIFY_TIME>=#{modifyTimeFrom,jdbcType=TIMESTAMP}
  AND MODIFY_TIME<=#{modifyTimeTo,jdbcType=TIMESTAMP}
  AND IC_ID like '%${icId}%'
  AND IC_ID=#{icIdEq,jdbcType=VARCHAR}
  AND NIL_URL like '%${nilUrl}%'
  AND NIL_URL=#{nilUrlEq,jdbcType=VARCHAR}
  AND SOURCE=#{source,jdbcType=VARCHAR}
  AND STUDY like '%${study}%'
  AND STUDY=#{studyEq,jdbcType=VARCHAR}
  AND PID like '%${pid}%'
  AND PID=#{pidEq,jdbcType=VARCHAR}
  AND PN like '%${pn}%'
  AND PN=#{pnEq,jdbcType=VARCHAR}
  AND GP like '%${gp}%'
  AND GP=#{gpEq,jdbcType=VARCHAR}

 ORDER BY ${_orderBy}

    SELECT COUNT(DISTINCT hospital_id) as temp_count
FROM cpf_case_history 
WHERE patient_id = #{patientId,jdbcType=INTEGER} 


AND create_time > #{createTime,jdbcType=TIMESTAMP}

    SELECT count(1) as value from(SELECT COUNT(DISTINCT hospital_id) as temp_count
FROM cpf_case_history 
WHERE patient_id = #{patientId,jdbcType=INTEGER} 


AND create_time > #{createTime,jdbcType=TIMESTAMP} 

    ) as subsql
